CREATE TABLE IF NOT EXISTS "customer" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "email" character varying NOT NULL, "document" character varying NOT NULL, "creation_date" TIMESTAMP NOT NULL DEFAULT now(), "name" character varying NOT NULL, CONSTRAINT "PK_d9445fbcb74d66c2fb7f2d2533e" PRIMARY KEY ("id", "email", "document"));
CREATE TABLE IF NOT EXISTS "order_status" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "status" text NOT NULL DEFAULT 'RECEIVED', CONSTRAINT "PK_8ea75b2a26f83f3bc98b9c6aaf6" PRIMARY KEY ("id"));
CREATE TABLE IF NOT EXISTS "order_item" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "product_id" character varying NOT NULL, "order_id" character varying NOT NULL, "quantity" integer NOT NULL, "total_price" numeric(3,2) NOT NULL, "discount" numeric(3,2) NOT NULL, CONSTRAINT "PK_d01158fe15b1ead5c26fd7f4e90" PRIMARY KEY ("id"));
CREATE TABLE IF NOT EXISTS "order" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "store_id" character varying NOT NULL, "customer_id" character varying NOT NULL, "creation_date" TIMESTAMP NOT NULL DEFAULT now(), "prevision_delivery_date" TIMESTAMP NOT NULL DEFAULT now(), "total_price" numeric(5,2) NOT NULL, "status_id" uuid, CONSTRAINT "PK_c0899e34ecfbf164afb35aec2e5" PRIMARY KEY ("id", "store_id", "customer_id"));
CREATE TABLE IF NOT EXISTS "product" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "store_id" character varying NOT NULL, "creation_date" TIMESTAMP NOT NULL DEFAULT now(), "name" character varying NOT NULL, "description" character varying NOT NULL, "categoryId" uuid, "categoryStoreId" character varying, CONSTRAINT "PK_0daef8d9bf14fa305f0c11c147c" PRIMARY KEY ("id", "store_id"));
CREATE TABLE IF NOT EXISTS "category" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "store_id" character varying NOT NULL, "name" character varying NOT NULL, "description" character varying NOT NULL, CONSTRAINT "PK_9bf3a72dd407c1d6211d9c1f0a7" PRIMARY KEY ("id", "store_id"));
CREATE TABLE IF NOT EXISTS "pricing" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "store_id" character varying NOT NULL, "product_id" character varying NOT NULL, "total_price" numeric(3,2) NOT NULL, CONSTRAINT "PK_59d5c0be55c41bc93a406ca017f" PRIMARY KEY ("id", "store_id"));
CREATE TABLE IF NOT EXISTS "store" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "creation_date" TIMESTAMP NOT NULL DEFAULT now(), "name" character varying NOT NULL, "description" character varying NOT NULL, CONSTRAINT "PK_f3172007d4de5ae8e7692759d79" PRIMARY KEY ("id"));
